import React, { useEffect, useState } from "react";
import { fetchImages } from "./api";
import { calculateNumber } from "./calculate";
import { Header } from "./header";
import { Footer } from "./footer";
import background from "./image/background.jpeg";
import { useForm } from 'react-hook-form';

function Image(props) {
	return (
		<div className="card">
      <div className="card-image">
        <figure className="image">
        <img src={props.src} alt="cute dog!" />
        </figure>
      </div>
    </div>
	);
}

function Loading() {
	return <p>Loading....</p>;
}

function Gallery(props) {
	const { urls } = props;
	if (urls == null) {
		return <Loading />
	}
	return (
		<div className="columns is-centered is-multiline">
			{urls.map((url) => {
				return (
					<div key={url} className="column is-3">
						<Image src={url} />
					</div>
				);
			})}
    </div>
	);
}

function Form(props) {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();
  const onSubmit = (data) => {
    props.onFormSubmit(data);
  }

	return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <div className="column">
        <div className="columns is-mobile">
          <div className="column is-half">
            <div className="field">
              <label className="label">ペットの種類</label>
              <div className="control is-expanded">
                <div className="select is-rounded is-focused is-info">
                  <select {...register("animalKind", { required: true })} defaultValue="shiba">
                    <option value="shiba">いぬ</option>
                    <option value="akita">ねこ</option>
                    <option value="african">アフリカの漢</option>
                    <option value="chow">ちゃうちゃう</option>
                    <option value="samoyed">さもえどちゃん</option>
                  </select>
                </div>
              </div>
            </div>
          </div>
          <div className="column is-half">
            <div className="field">
              <label className="label">ペットの年齢（年）</label>
              <div className="control is-expanded">
                <input
                  className="input is-rounded is-focused is-info"
                  {...register('petAge', { min: 0, max: 30 })}
                />
                {errors.petAge && "0から30才の間で選んでください"}
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="column">
        <div className="columns is-mobile">
            <div className="column">
            <div className="native-flex">
              <div className="field">
                <label className="label">年に何回いっしょに遊びますか？</label>
                <div className="control is-expanded">
                  <input
                    className="input is-rounded is-focused is-info"
                    {...register('numberOfPlay', { required: true })}
                  />
                  {errors.numberOfPlay && "遊ぶ回数を選んでください"}
                </div>
              </div>
            </div>
          </div>

          <div className="icon is-small is-left">
            <i className="fas fa-globe"></i>
          </div>
          <div className="column">
            <div className="control">
              <button type="submit" className="button is-primary">
                結果
              </button>
            </div>
          </div>
        </div>
      </div>
    </form>
	)
}

function Result(props) {
  const { resultNum } = props;
  return (
    <div className="columns is-centered">
      {resultNum}
    </div>
  );
}

function Main() {
	const [urls, setUrls] = useState(null);
  const [resultNum, setResultNum] = useState(null);
  const [isResulted, setIsResulted] = useState(false);

	useEffect(() => {
    fetchImages("shiba").then((urls) => {
      setUrls(urls);
    });
  }, []);

	function reloadResults(data) {
		fetchImages(data.animalKind).then((urls) => {
			setUrls(urls);
		});

    calculateNumber(data).then((resultNum) => {
      setResultNum(resultNum);
    });

    setIsResulted(true);
	};

  function defaultResult() {
    setIsResulted(false);
  };

  const result = (
    <div className="columns is-centered">
      <p>あなたのペットは後</p>
      <h2>{resultNum}</h2>
      <p>回いっしょに遊べます。</p>
      <button onClick={defaultResult} className="button is-primary">
        スタートに戻る
      </button>
    </div>
  )

	return (
		<main>
			<section className="section">
				<div className="container">
          <div className="columns has-background-white is-centered">
            {isResulted ? result : <Form onFormSubmit={reloadResults}/>}
          </div>
				</div>
			</section>
			<section className="section">
				<div className="container">
					<Gallery urls={urls} />
				</div>
			</section>
		</main>
	);
}

function App() {
  const containerStyle = {
    backgroundImage: `url(${background})`,
    backgroundPosition: "center center",
    backgroundRepeat: 'no-repeat',
    backgroundAttachment: 'fixed',
    backgroundSize: 'cover',
    backgroundColor: '#464646'
  }
	return (
		<div style={containerStyle}>
			<Header />
      <Main />
      <Footer />
    </div>
	);
}

export default App;
