import React, { useEffect, useState } from "react";
import { fetchImages } from "./api";
import background from "./image/background.jpeg";

function Header() {
	return (
		<header className="hero is-small is-info is-hold">
			<div className="hero-body">
				<div className="container">
					<h1 className="title">家族と遊ぼう</h1>
				</div>
			</div>
		</header>
	);
}

function Image(props) {
	return (
		<div className="card">
      <div className="card-image">
        <figure className="image">
        <img src={props.src} alt="cute dog!" />
        </figure>
      </div>
    </div>
	);
}

function Loading() {
	return <p>Loading....</p>;
}

function Gallery(props) {
	const { urls } = props;
	if (urls == null) {
		return <Loading />
	}
	return (
		<div className="columns is-vcentered is-multiline">
			{urls.map((url) => {
				return (
					<div key={url} className="column is-3">
						<Image src={url} />
					</div>
				);
			})}
    </div>
	);
}

function Form(props) {
	function handleSubmit(event) {
		event.preventDefault();
		const { breed } = event.target.elements;
		props.onFormSubmit(breed.value);
	}
	return (
		<div>
			<form onSubmit={handleSubmit}>
        <div className="container">
          <div className="field has-addons">
            <div className="control is-expanded">
              <div className="select">
                <select name="breed" defaultValue="shiba">
                  <option value="shiba">しば！</option>
                  <option value="akita">秋田ちゃん</option>
                  <option value="african">アフリカの漢</option>
                  <option value="chow">ちゃうちゃう</option>
                  <option value="samoyed">さもえどちゃん</option>
                </select>
              </div>
              <div className="control">
                <button type="submit" className="button is-primary">
                  結果
                </button>
              </div>
            </div>
          </div>
        </div>
			</form>
		</div>
	)
}

function Main() {
	const [urls, setUrls] = useState(null);
	useEffect(() => {
    fetchImages("shiba").then((urls) => {
      setUrls(urls);
    });
  }, []);

	function reloadImages(breed) {
		fetchImages(breed).then((urls) => {
			setUrls(urls);
		});
	}
	return (
		<main>
			<section className="section">
				<div className="container">
					<Form onFormSubmit={reloadImages}/>
				</div>
			</section>
			<section className="section">
				<div className="container">
					<Gallery urls={urls} />
				</div>
			</section>
		</main>
	);
}

function Footer() {
	return (
		<footer className="footer">
			<div className="content has-text-centered">
				<b>なぜやるか？</b>
				<p>
					僕自身ペットわんちゃんを飼ってきました。小さい頃から一緒に成長してきて家族同然に考えていました。しかし、中学生の頃体調が急変して亡くなってしまいます。
				</p>
        <p>
					今考えると犬の寿命は人よりも短く、寿命が近いのはわかりきったことでした。しかし、手遅れになって残ったのは大きな後悔の念だけです。
				</p>
        <p>
          もっと一緒に遊んであげたかった。そんな悔しい想いを抱く人を増やさないタメにも。一緒に遊んで欲しかったペットのためにもこのサイトを作っています。
				</p>
			</div>
		</footer>
	)
}

function App() {
  const containerStyle = {
    backgroundImage: `url(${background})`,
    backgroundPosition: "center center",
    backgroundRepeat: 'no-repeat',
    backgroundAttachment: 'fixed',
    backgroundSize: 'cover',
    backgroundColor: '#464646'
  }
	return (
		<div style={containerStyle}>
			<Header />
      <Main />
      <Footer />
    </div>
	);
}

export default App;
