import React, { useEffect, useState } from "react";
import { fetchImages } from "./api";
import { calculateNumber } from "./calculate";
import { Header } from "./header";
import { Footer } from "./footer";
import { Form } from "./form";
import background from "./image/background.jpeg";

function Image(props) {
	return (
		<div className="card">
      <div className="card-image">
        <figure className="image">
        <img src={props.src} alt="cute dog!" />
        </figure>
      </div>
    </div>
	);
}

function Loading() {
	return <p>Loading....</p>;
}

function Gallery(props) {
	const { urls } = props;
	if (urls == null) {
		return <Loading />
	}
	return (
		<div className="columns is-centered is-multiline">
			{urls.map((url) => {
				return (
					<div key={url} className="column is-3">
						<Image src={url} />
					</div>
				);
			})}
    </div>
	);
}


function Result(props) {
  const { resultNum } = props;
  return (
    <div className="columns is-centered">
      {resultNum}
    </div>
  );
}

function Main() {
	const [urls, setUrls] = useState(null);
  const [resultNum, setResultNum] = useState(null);
  const [isResulted, setIsResulted] = useState(false);

	useEffect(() => {
    fetchImages("shiba").then((urls) => {
      setUrls(urls);
    });
  }, []);

	function reloadResults(data) {
		fetchImages(data.animalKind).then((urls) => {
			setUrls(urls);
		});

    calculateNumber(data).then((resultNum) => {
      setResultNum(resultNum);
    });

    setIsResulted(true);
	};

  function defaultResult() {
    setIsResulted(false);
  };

  const result = (
    <div className="columns is-centered">
      <p>あなたのペットは残り</p>
      <h2>{resultNum}</h2>
      <p>回いっしょに遊べます。</p>
      <button onClick={defaultResult} className="button is-primary">
        スタートに戻る
      </button>
    </div>
  )

	return (
		<main>
			<section className="section">
				<div className="container">
          <div className="columns has-background-white is-centered">
            {isResulted ? result : <Form onFormSubmit={reloadResults}/>}
          </div>
				</div>
			</section>
			<section className="section">
				<div className="container">
					<Gallery urls={urls} />
				</div>
			</section>
		</main>
	);
}

function App() {
  const containerStyle = {
    backgroundImage: `url(${background})`,
    backgroundPosition: "center center",
    backgroundRepeat: 'no-repeat',
    backgroundAttachment: 'fixed',
    backgroundSize: 'cover',
    backgroundColor: '#464646'
  }
	return (
		<div style={containerStyle}>
			<Header />
      <Main />
      <Footer />
    </div>
	);
}

export default App;
