function Main() {
	const [urls, setUrls] = useState(null);
	useEffect(() => {
    fetchImages("shiba").then((urls) => {
      setUrls(urls);
    });
  }, []);

	function reloadImages(breed) {
		fetchImages(breed).then((urls) => {
			setUrls(urls);
		});
	}
	return (
		<main>
			<section className="section">
				<div className="container">
					<Form onFormSubmit={reloadImages}/>
				</div>
			</section>
			<section className="section">
				<div className="container">
					<Gallery urls={urls} />
				</div>
			</section>
		</main>
	);
}

